  [ d(15, 'second'), d(10, 'second') ],
  [ d(45, 'second'), d(30, 'second') ],
  [ d(3, 'minute'), d(1, 'minute') ],
  [ d(9, 'minute'), d(5, 'minute') ],
  [ d(20, 'minute'), d(10, 'minute') ],
  [ d(45, 'minute'), d(30, 'minute') ],
  [ d(2, 'hour'), d(1, 'hour') ],
  [ d(6, 'hour'), d(3, 'hour') ],
  [ d(24, 'hour'), d(12, 'hour') ],
  [ d(1, 'week'), d(1, 'd') ],
  [ d(3, 'week'), d(1, 'week') ],
  [ d(1, 'year'), d(1, 'month') ],
  [ Infinity, d(1, 'year') ]
