    indices(metricName) {
      const metric = metrics[metricName];
      const buckets = item[metricName].buckets;
      const { lastVal } = metricCalculator(buckets, metric);
      return {
        metric: pickMetricFields(metric),
        last: lastVal
      };
    }

  };

  return reducers[type];
